mapper.select:
select * from image_table,recommended_table,image_recommended

    image_table.image_id = image_recommended.image_id
    and image_recommended.recommended_id = recommended_table.recommended_id
    and image_table.source=0

        and image_table.image_name like concat('%',#{imageName},'%')


        and image_table.image_system= #{imageSystem}


        and recommended_table.recommended_cpu= #{recommendedCpu}


        and recommended_table.recommended_memory = #{recommendedMemory}


        and recommended_table.recommended_systemDisk = #{recommendedSystemDisk}


        and recommended_table.recommended_dataDisk = #{recommendedDataDisk}


        and image_table.image_id in(select image_label.image_id from image_label,label_table
        where image_label.label_id=label_table.label_id
        and label_table.label_name in

         #{label}

         group by image_id
        having count(image_id)=#{count})

select * from image_table,recommended_table,image_recommended

    image_table.image_id = image_recommended.image_id
    and image_recommended.recommended_id = recommended_table.recommended_id
    and image_table.source=1

        and image_table.image_name like concat('%',#{imageName},'%')


        and image_table.image_system= #{imageSystem}


        and recommended_table.recommended_cpu= #{recommendedCpu}


        and recommended_table.recommended_memory = #{recommendedMemory}


        and recommended_table.recommended_systemDisk = #{recommendedSystemDisk}


        and recommended_table.recommended_dataDisk = #{recommendedDataDisk}


        and image_table.image_id in(select image_label.image_id from image_label,label_table
        where image_label.label_id=label_table.label_id
        and label_table.label_name in

            #{label}

        group by image_id
        having count(image_id)=#{count})
