mapper.select:
select  distinct image_table.image_id,image_name,image_system,image_introduce
               ,image_label.recommended_id,recommended_cpu,recommended_memory,
                recommended_data_disk,image_remark,image_table.user_id
from image_table,recommended_table,image_label

    image_table.image_id = image_label.image_id
    and recommended_table.recommended_id = image_label.recommended_id
    and image_table.source=0

        and image_table.image_remark like concat('%',#{imageName},'%')


        and image_table.image_system= #{imageSystem}


        and recommended_table.recommended_cpu= #{recommendedCpu}


        and recommended_table.recommended_memory = #{recommendedMemory}


        and recommended_table.recommended_data_disk = #{recommendedDataDisk}


        and (image_table.image_id,image_label.recommended_id) in(select image_label.image_id,image_label.recommended_id
        from image_label,label_table
        where image_label.label_id=label_table.label_id
        and label_table.label_name in

         #{label}

         group by image_id,recommended_id
        having count(image_id)=#{count}
        )

select  distinct image_table.image_id,image_name,image_system,image_introduce
,image_label.recommended_id,recommended_cpu,recommended_memory
               ,recommended_data_disk,image_remark,image_table.user_id
from image_table,recommended_table,image_label

    image_table.image_id = image_label.image_id
    and recommended_table.recommended_id = image_label.recommended_id
    and image_table.source=1

        and image_table.image_remark like concat('%',#{imageName},'%')


        and image_table.image_system= #{imageSystem}


        and recommended_table.recommended_cpu= #{recommendedCpu}


        and recommended_table.recommended_memory = #{recommendedMemory}


        and recommended_table.recommended_data_disk = #{recommendedDataDisk}


        and (image_table.image_id,image_label.recommended_id) in(select image_label.image_id,image_label.recommended_id
        from image_label,label_table
        where image_label.label_id=label_table.label_id
        and label_table.label_name in

            #{label}

        group by image_id,recommended_id
        having count(image_id)=#{count}
        )

select  distinct image_table.image_id,image_name,image_system,image_introduce
,image_label.recommended_id,recommended_cpu,recommended_memory
               ,recommended_data_disk,image_remark,image_table.user_id
from image_table,recommended_table,image_label

    image_table.image_id = image_label.image_id
    and recommended_table.recommended_id = image_label.recommended_id
    and image_table.user_id = #{userId}
    and image_table.source= 1

        and image_table.image_remark like concat('%',#{imageName},'%')


        and image_table.image_system= #{imageSystem}


        and recommended_table.recommended_cpu= #{recommendedCpu}


        and recommended_table.recommended_memory = #{recommendedMemory}


        and recommended_table.recommended_data_disk = #{recommendedDataDisk}


        and (image_table.image_id,image_label.recommended_id) in(select image_label.image_id,image_label.recommended_id
        from image_label,label_table
        where image_label.label_id=label_table.label_id
        and label_table.label_name in

            #{label}

        group by image_id,recommended_id
        having count(image_id)=#{count}
        )
